import React, { useState } from 'react';
import styled from 'styled-components';
import Carta from '../Carta/Carta';

const BarajaContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  position: relative;
  height: 500px;
  width: 500px;
`;

const Baraja = ({ cartas, onAgregarCarta, onCartaHover }) => {
  const [cartasEnBaraja, setCartasEnBaraja] = useState(cartas);

  const agregarCarta = (nuevaCarta) => {
    setCartasEnBaraja((prevCartas) => [...prevCartas, nuevaCarta]);
    if (onAgregarCarta) {
      onAgregarCarta(nuevaCarta);
    }
  };

  return (
    <BarajaContainer>
      {cartasEnBaraja.map((carta, index) => {
        const numCartas = cartasEnBaraja.length;
        const isEven = numCartas % 2 === 0;
        const middleIndex = isEven ? numCartas / 2 - 1 : Math.floor(numCartas / 2);
        const offset = index - middleIndex;

        // Ajuste de altura basado en si el número de cartas es par o impar
        const topPosition = isEven ? Math.abs(offset) * 10 : Math.abs(offset) * 10;

        return (
          <Carta
            key={index}
            numero={carta.numero}
            palo={carta.palo}
            onMouseEnter={() => onCartaHover(carta)}
            onMouseLeave={() => onCartaHover(null)}
            style={{
              position: 'absolute',
              left: `${index * 70}px`, // Controla la posición horizontal
              top: `${topPosition}px`,   // Controla la posición vertical para crear el abanico
              zIndex: index
            }}
          />
        );
      })}
    </BarajaContainer>
  );
};

export default Baraja;
