import React from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import Carta from './components/Carta/Carta.js';
import Login from './components/Login'; // Importa el componente de inicio de sesión
import './App.css';

const Container = styled.div`
  padding: 20px;
`;

const PantallaInicial = () => {
  const history = useHistory();

  const handleComenzar = () => {
    history.push('/login'); // Navega a la ruta de inicio de sesión sin recargar
  };

  return (
    <div className="pantalla-inicial">
      <h1>SEVEN & HALF</h1>
      <h2>¡Ganar dinero nunca fue tan fácil!</h2>
      <button onClick={handleComenzar}>Comenzar</button>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={PantallaInicial} />
        <Route path="/login" component={Login} />
      </Switch>
      <Container>
        <Carta palo="trebol" numero="J" />
      </Container>
    </Router>
  );
};

export default App;
