import React, { useState } from 'react';
import styled from 'styled-components';
import Carta from '../Carta/Carta';

const BarajaContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  position: relative;
  height: 500px;
  width: 500px;
`;

const Baraja = ({ cartas, onAgregarCarta, onCartaHover }) => {
  const [cartasEnBaraja, setCartasEnBaraja] = useState(cartas);

  const agregarCarta = (nuevaCarta) => {
    setCartasEnBaraja((prevCartas) => [...prevCartas, nuevaCarta]);
    if (onAgregarCarta) {
      onAgregarCarta(nuevaCarta);
    }
  };

  return (
    <BarajaContainer>
      {cartasEnBaraja.map((carta, index) => {
        // Calcular el desplazamiento horizontal y vertical
        const middleIndex = Math.floor(cartasEnBaraja.length / 2); // Encuentra el índice central
        const offset = index - middleIndex; // Determina cuán lejos está la carta del centro
        const topPosition = Math.abs(offset) * 10; // Altura más baja en los bordes, más alta al centro

        return (
          <Carta
            key={index}
            numero={carta.numero}
            palo={carta.palo}
            onMouseEnter={() => onCartaHover(carta)}
            onMouseLeave={() => onCartaHover(null)}
            style={{
              position: 'absolute',
              left: `${index * 70}px`, // Controla la posición horizontal
              top: `${topPosition}px`,   // Controla la posición vertical para crear el abanico
              zIndex: index
            }}
          />
        );
      })}
    </BarajaContainer>
  );
};

export default Baraja;
