import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [name, setName] = useState('');
  const [money, setMoney] = useState(0);
  const navigate = useNavigate(); // Hook para navegar a otra ruta

  const handleLogin = () => {
    // Aquí puedes validar el login o simplemente redirigir
    navigate('/game', { state: { name, money } }); // Redirige
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="number"
        placeholder="Amount of money"
        value={money}
        onChange={(e) => setMoney(parseFloat(e.target.value))}
      />
      <button onClick={handleLogin}>Start the game</button>
    </div>
  );
};

export default Login;
