import React from "react";
import styled from "styled-components";
import Icon from "../Icon";
import COLORS from "../../styles/colors";

const Boton = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: green;
  color: white;
  font-size: 28px;
  border-style: none;
  padding: 5px 10px;
  border-radius: 5%;
  border: 2px solid ${COLORS.neutral600}; /* Borde ajustable */
  border-radius: 8px; /* Ajusta el radio para esquinas redondeadas */
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2); /* Sombra ajustable */
`;

const Button = ({ label, icon, showIcon }) => {
  return (
    <Boton>
      {showIcon && <Icon iconName={icon}></Icon>}
      {label}
    </Boton>
  );
};

export default Button;
