import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from "styled-components";


const Input = styled.input`
  padding: 10px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 200px;
  margin-right: 10px;

  
  &::-webkit-outer-spin-button,
  &::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  
  -moz-appearance: textfield;
`;

const Login = () => {
  const [name, setName] = useState('');
  const [money, setMoney] = useState(0);
  const navigate = useNavigate(); // Hook per navegar a una altra ruta

  const handleLogin = () => {
   
    navigate('/game', { state: { name, money } }); // Redirigeix a TableGame i envia les dades del login
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <I
        type="number"
        placeholder="Amount of money"
        value={money}
        onChange={(e) => setMoney(parseFloat(e.target.value))}
      />
      <button onClick={handleLogin}>Start the game</button>
    </div>
  );  
};

export default Login;
